import re
from selenium.webdriver.common.by import By
from forms.utils.form_utils import set_value_by_id, select_option_by_id

class QualificationsSecondSchoolStudies:
    def __init__(self, driver, data):
        self.driver = driver
        self.data = data

    def run(self):
        students = self.data
        driver = self.driver
        personal_info = students[-1][0]
        
        # Set secondary school values
        set_value_by_id(driver, "IPQ_APONSS1", personal_info.get('secondary_school', ''))
        set_value_by_id(driver, "IPQ_APONSS2", personal_info.get('school_location', ''))
        select_option_by_id(driver, "IPQ_APONSS3", personal_info.get('completion_year', ''))
        
        # Add additional fields based on the structure in the HTML
        set_value_by_id(driver, "IPQ_APMOTHRS", personal_info.get('atar_rank', ''))
