import re
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from forms.utils.form_utils import set_value_by_id, select_option_by_id, check_button_by_id, select_radio_by_value

class QualificationsAcademicQualifications:
    def __init__(self, driver, data):
        self.driver = driver
        self.data = data

    def run(self):
        students = self.data
        driver = self.driver
        personal_info = students[-1][0]
        
        # Get academic qualifications from data
        academic_qualifications = personal_info.get('academic_qualifications', [])
        
        # If no qualifications provided, add a default one
        if not academic_qualifications:
            academic_qualifications = [{
                'qualification_type': 'Bachelors degree',
                'course_title': 'Bachelor of Science',
                'institution': 'University Example',
                'country': personal_info.get('Country', 'China (Excludes SARS and Taiwan)'),
                'start_date': '01/09/2018',
                'completion_date': '30/06/2022',
                'length_months': '36',
                'grade': 'First Class',
                'completed': True,
                'full_time': True
            }]
        
        # Fill in the first qualification (the form always shows the first one)
        qualification = academic_qualifications[0]
        
        # Qualification type
        qual_type = qualification.get('qualification_type', 'Bachelors degree')
        select_option_by_id(driver, "IPQ_APONQ1A", qual_type)
        
        # Course title
        course_title = qualification.get('course_title', '')
        set_value_by_id(driver, "IPQ_APONQ1B", course_title)
        
        # Institution
        institution = qualification.get('institution', '')
        set_value_by_id(driver, "IPQ_APONQ1C", institution)
        
        # Country
        country = qualification.get('country', personal_info.get('Country', 'China (Excludes SARS and Taiwan)'))
        select_option_by_id(driver, "IPQ_APONQ1D", country)
        
        # Start date
        start_date = qualification.get('start_date', '01/09/2018')
        set_value_by_id(driver, "IPQ_APONQ1E", start_date)
        
        # Completion date
        completion_date = qualification.get('completion_date', '30/06/2022')
        set_value_by_id(driver, "IPQ_APONQ1F", completion_date)
        
        # Standard length in months
        length_months = qualification.get('length_months', '36')
        set_value_by_id(driver, "IPQ_APONQ1G", length_months)
        
        # Class or grade
        grade = qualification.get('grade', '')
        set_value_by_id(driver, "IPQ_APONQ1H", grade)
        
        # Completion status
        completed = qualification.get('completed', True)
        if completed:
            select_radio_by_value(driver, "IPQ_APONQ1I", "COMPLT")
        else:
            select_radio_by_value(driver, "IPQ_APONQ1I", "NOT")
        
        # Study mode
        full_time = qualification.get('full_time', True)
        if full_time:
            select_radio_by_value(driver, "IPQ_APONQ1J", "F")
        else:
            select_radio_by_value(driver, "IPQ_APONQ1J", "P")
        
        # If there are more qualifications, check the box to add another
        if len(academic_qualifications) > 1:
            check_button_by_id(driver, "agreement1")


